import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Link,
  useParams,
  useNavigate,
  useSearchParams,
} from "react-router-dom";
import {
  Row,
  Col,
  Image,
  ListGroup,
  Button,
  Form,
  Card,
  ListGroupItem,
} from "react-bootstrap";
import { addToCart } from "../actionCreators/cartActionCreators";
import Message from "../components/Message";

function CartScreen() {
  const { id } = useParams();

  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;

  const [searchParams] = useSearchParams();
  let qty = 0;
  if (searchParams) {
    qty = searchParams.get("qty");
  } else {
    qty = 1;
  }

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(addToCart(id, qty));
  }, [dispatch, id, qty]);

  // const handleIncrement = () => {
  //   dispatch(addToCart(item.product_id, item.qty + 1));
  // };

  // const handleDecrement = () => {
  //   item.qty > 1 ? addToCart(item.product_id, item.qty - 1) : addToCart(item.product_id, 0)
  // };

  return (
    <Row>
      <Col md={8}>
        <h1>Shopping Cart</h1>
        {cartItems.length === 0 ? (
          <Message variant="info">
            Your cart is empty <Link to="/">Go Back</Link>
          </Message>
        ) : (
          <ListGroup variant="flush">
            {cartItems.map((item) => (
              <ListGroupItem key={item.product_id}>
                <Row>
                  <Col md={2}>
                    <Image src={item.image} alt={item.name} fluid rounded />
                  </Col>
                  <Col md={3}>
                    <Link to={`/product/${item.product_id}`}>{item.title}</Link>
                  </Col>
                  <Col md={2}>${item.price}</Col>
                  <Col className="mt-1">
                    <Button
                      bsPrefix="adjust-btn"
                      type="button"
                      size="sm"
                      onClick={() => {
                        Number(item.qty) > 1 ? addToCart(item.product_id, Number(item.qty) - 1) : addToCart(item.product_id, 0)
                      }}
                    >
                      <i className="fa fa-minus"></i>
                    </Button>
                  </Col>
                  <Col className="mt-1">
                    <Form.Label>{item.qty}</Form.Label>
                  </Col>
                  <Col className="mt-1">
                    <Button
                      bsPrefix="adjust-btn"
                      type="button"
                      size="sm"
                      onClick={() => {
                        dispatch(addToCart(item.product_id, Number(item.qty) + 1));
                      }}
                    >
                      <i className="fa fa-plus"></i>
                    </Button>
                  </Col>
                </Row>
              </ListGroupItem>
            ))}
          </ListGroup>
        )}
      </Col>
    </Row>
  );
}

export default CartScreen;
